package com.common.util;public class JSON<T> {	private boolean success;	private String msg = "";	private T rows;	public JSON() {		super();	}	/**	 * 直接返回是否成功	 * @param success	 */	public JSON(boolean success) {		this.setSuccess(success);	}	/**	 * 成功,直接返回数据	 * @param data	 */	public JSON(T data) {		this.setRows(data);	}	/**	 * 失败,需要返回提示信息	 * @param success	 * @param msg	 */	public JSON(boolean success, String msg) {		this.setSuccess(success);		this.setMsg(msg);	}	public boolean getSuccess() {		return success;	}	public void setSuccess(boolean success) {		this.success = success;	}	public String getMsg() {		return msg;	}	public void setMsg(String msg) {		this.msg = msg;	}		public T getRows() {		return rows;	}	public void setRows(T rows) {		this.rows = rows;	}}